                                        } else {
                                            setError("Invalid credentials");
                                        }
                                    });
                                }
                            }}
                        >
                            Log in
                        </Button>
                        <p className="mt-3">
                            New to the platform?{" "}
                            <a href="/register" className="new-account-link">
                                Create an account
                            </a>
                        </p>
                    </div>
                </Form>
            </div>
        </div>
    );
}

