import { Modal } from "react-bootstrap";
import DatePicker, {DateObject} from "react-multi-date-picker";
import React, {useState} from "react";

function AnnouncementModal({ announcement, showModal, handleModalToggle }) {
    const [selectedDays, setSelectedDays] = useState([]);
    const [error, setError] = useState("");
    const [success, setSuccess] = useState("");

    const handleScheduleClick = async () => {
        if (selectedDays.length === 0) {
            setError("Please fill in all the details");
            return;
        }

        const selectedDates = selectedDays.map((date) => {
                let newDate = new Date(date.toString());
                let newUTCDate = new Date(newDate.getTime() - newDate.getTimezoneOffset() * 60000);
                console.log(newUTCDate);
                return newUTCDate;
            }
        );

        // Handle the logic for scheduling here
        console.log(selectedDays);
        const reservation = {
            announcementId: announcement.id,
            selectedDates: selectedDates
            //Integer announcementId;
            //Set<LocalDate> selectedDates;
        }

        console.log(reservation);

        try {
            const token = localStorage.getItem("token");
            const response = await fetch("reservations", {
                method: "PUT",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json",
                    Authorization: `Bearer ${token}`
                },
                body: JSON.stringify(reservation)
            });

            if (response.ok) {
                setSuccess("Reservations added successfully");
            } else {
                setError("An error occurred. Please try again.");
            }
            console.log(JSON.stringify(reservation));

        } catch (error) {
            setError("An error occurred. Please try again.");
        }


    };

    const handleOwnerClick = () => {
        // Handle the logic for opening the owner's profile here
    };

    function isAvailable(date) {
        let actualDate = new Date(date.toString());
        actualDate.setHours(0, 0, 0, 0);

        // check that announcement.reservations has a reservation with the same date and not a user
        for(let i = 0; i < announcement.reservations.length; i++) {
            let reservationDate = new Date(announcement.reservations[i].date);
            reservationDate.setHours(0, 0, 0, 0);

            //console.log(date.getTime());

            console.log(actualDate);
            console.log(reservationDate);
            console.log(actualDate.getTime() === reservationDate.getTime());

            if(actualDate.getTime() === reservationDate.getTime() && announcement.reservations[i].user === null) {
                return true;
            }
        }

        return false;
    }

    return (
        <Modal show={showModal} onHide={handleModalToggle}>
            <Modal.Header closeButton>
                <Modal.Title>{announcement.title}</Modal.Title>
            </Modal.Header>
            <Modal.Body>
                <div>
                    <p>Owner: <span className="owner-link" onClick={handleOwnerClick}>{announcement.owner}</span></p>
                </div>
                <p>Description: {announcement.description}</p>
                <p>Current rating: {announcement.rate}/5 &#x2605;</p>
                <p>
                    Date: {announcement.startDate} - {announcement.endDate}
                </p>
                <DatePicker
                    multiple
                    value={selectedDays}
                    onChange={(value) => {setSelectedDays(value)}}
                    mapDays={({ date }) => {

                        if(!isAvailable(date)) return {
                            disabled: true,
                            style: { color: "#ccc" },
                        }
                    }}

                    minDate={new DateObject()}
                />
                {error && <p className="text-danger">{error}</p>}
                {success && <p className="text-success">{success}</p>}
                {/* Include any additional details here */}
            </Modal.Body>
            <Modal.Footer>
                <button className="btn btn-primary" onClick={handleScheduleClick}>
                    Schedule
                </button>
                <button className="btn btn-secondary" onClick={handleModalToggle}>
                    Close
                </button>
            </Modal.Footer>
        </Modal>
    );
}

export default AnnouncementModal;
