import React, {useEffect, useState} from 'react';
import { Container, Row, Col, Card, Button, Form, InputGroup } from 'react-bootstrap';
import CustomNavbar from "./generics/CustomNavbar";
import 'react-phone-input-2/lib/style.css';
import PhoneInput from 'react-phone-input-2';
import { useNavigate } from "react-router-dom";

const UserDataTile = ({token}) => {

    const [profileForm, setProfileForm] = useState({
        firstName: "",
        lastName: "",
        username: "",
        email: "",
        phoneNumber: ""
    });

    const [error, setError] = useState("");

    const handleUpdate = (event) => {
        const name = event.target.name;
        const value = event.target.value;

        setProfileForm({ ...profileForm, [name]: value });
    };

    const handleSave = (event) => {
        event.preventDefault();

        try{
            fetch("user", {
                method: "PUT",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json",
                    'Authorization': localStorage.getItem("token")
                },
                body: JSON.stringify(profileForm),
            }).then(response => {
                if(!response.ok) {
                    setError("Invalid credentials");
                }
            }).catch(error => {
                setError("An error occurred. Please try again.");
            });
        } catch (error) {
            setError("An error occurred. Please try again.");
        }
    }

    const [phone, setPhone] = useState('');

    useEffect(() => {
        try {
            fetch("user", {
                method: "POST",
                headers: {
                    Accept: "application/json",
                    "Content-Type": "application/json",
                    'Authorization': token
                }
            }).then(response => {
                if(response.status == 200) {
                    return response.json();
                }
                throw new Error('Something went wrong');
            }).then(data => {
                setProfileForm({
                    firstName: data.firstName,
                    lastName: data.lastName,
                    username: data.username,
                    email: data.email,
                    phoneNumber: data.phoneNumber
                });
            });
        } catch (error) {
            setError("An error occurred. Please try again.");
        }
    }, []);

    return (
        <Container>
            <CustomNavbar/>
            <Row className="justify-content-md-center mt-5">
                <Col md={8}>
                    <Card>
                        <Card.Body>
                            <Form>
                                <Row>
                                    <Col>
                                        <Form.Group controlId="firstName">
                                            <Form.Label>First Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                name="firstName"
                                                onChange={handleUpdate}
                                                value={profileForm.firstName}
                                                placeholder="Enter your first name" />
                                        </Form.Group>
                                    </Col>
                                    <Col>
                                        <Form.Group controlId="lastName">
                                            <Form.Label>Last Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                name="lastName"
                                                onChange={handleUpdate}
                                                value={profileForm.lastName}
                                                placeholder="Enter your last name" />
                                        </Form.Group>
                                    </Col>
                                </Row>
                                <Form.Group controlId="username">
                                    <Form.Label>Username</Form.Label>
                                    <Form.Control
                                        type="text"
                                        name="username"
                                        onChange={handleUpdate}
                                        value={profileForm.username}
                                        placeholder="Enter your username" />
                                </Form.Group>
                                <Form.Group controlId="email">
                                    <Form.Label>Email</Form.Label>
                                    <Form.Control
                                        type="email"
                                        name="email"
                                        onChange={handleUpdate}
                                        value={profileForm.email}
                                        placeholder="Enter your email" />
                                </Form.Group>
                                <Form.Group controlId="phoneNumber">
                                    <Form.Label>Phone Number</Form.Label>
                                    <InputGroup>
                                        <PhoneInput
                                            country={'ro'} // Set the default country
                                            value={profileForm.phoneNumber}
                                            onChange={(value) => handleUpdate({ target: { name: "phoneNumber", value } })}
                                            inputProps={{
                                                className: 'form-control',
                                                style: { width: '100%' }
                                            }}
                                        />
                                    </InputGroup>
                                </Form.Group>
                                <Row className="justify-content-end">
                                    <Col xs={6} md={4} lg={3}>
                                        <Button
                                            variant="primary"
                                            type="submit"
                                            onClick={handleSave}
                                            className="float-end">Save Changes</Button>
                                    </Col>
                                </Row>
                            </Form>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    );
}

export default ProfilePage;
